INSERT INTO `system_archives` VALUES ('83acb714ec1b4633b5d2aad0626b2c5c', '关于深淘滩 低作堰的一点浅见', 'p', '20200803\\1e6c4bba88454c4099d7f687b864dde6.jpg', null, '深淘滩，低作堰 来自于都江堰水利工程。 　　关于都江堰介绍：秦国蜀郡太守李冰父子在公元前256年修建的水利工程。都江堰是李冰父子奥秘除了巧夺天工的工程布局外，更主要的是遵循了“乘势利导、因时制宜”的治水指导思想、“岁必一修”的管理制度，“遇难弯截角、逢正抽心”的治河原则，以及“砌鱼嘴立湃缺，深淘滩、低作堰”的引水、防沙、泄洪之管理经验和治堰准则。当时成都平原是悬江 ，落差大， 西洪灾，东部旱灾。 宝瓶口：分流， 鱼嘴：解决东高西低，控制流量，河床内深外浅 飞沙堰：弯道，超过堰泥沙 流入外江，防止内江堵塞', '934cd2841e7e41c3b41dc4f421641778', '1', '1', '14', '0', '1', '.292k186t.d90r0e70.3zc2e649', '9f4b807db2e94670bb02cdc212ea7389', '2020-08-03 02:33:15', null, '2020-08-04 09:20:32');
