INSERT INTO `system_category` VALUES ('37d8fa62d78c431ea16e1a00055085f9', '集成案例', '', '03f9010p', '.k5n59rrq.03f9010p', 'c17ddcad9fb149a6bf90f7bba0e0696b', '', '', '1', '10', '1', 'document', '/kh_document.html', '', '', '', 'ue', '', '<h1 style=\"box-sizing: border-box;font-size: 36px;margin: 20px 0px 10px 28px;font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">1<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">系统集成板块概述</span></h1><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">当今各行各业都面临着异常激烈的竞争，机遇与挑战并存。如何增强核心竞争力，如何提高运营效率和客户满意度，如何控制并降低成本，如何获得业务的增长，成为企业负责人最关心的话题。</span></p><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">信息技术在各行各业发展中起到的作用日益凸显，信息化为我们带来了高效率的业务模式，信息化为企业的高速发展提供了最新的技术保证，如何让信息技术转化为高生产力，并最终成为核心竞争力，将是企业不断思考的话题。</span></p><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">同天科技基于对信息化建设和发展的深刻洞察，以及企业信息化建设的特点，长期以来致力于为</span><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">政府、金融、企业、能源、教育、电力等各大行业</span><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">提供融合IT咨询服务、信息系统运行维护服务、企业网建设、融合视讯、云计算、数据中心建设、信息安全服务、灾备服务等内容的ICT整体解决方案。</span></p><p style=\"text-align:center\"><img title=\"1596508277825051303.png\" alt=\"10101930q07t.png\" src=\"/uploads/20200804/1596508277825051303.png\"/></p><h1 style=\"box-sizing: border-box;font-size: 36px;margin: 20px 0px 10px 28px;font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">2<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">解决方案类</span></h1><h2 style=\"box-sizing: border-box;font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">2.1<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">企业网解决方案</span></h2><h3 style=\"box-sizing: border-box;font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">2.1.1&nbsp;</span><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">方案概述</span></h3><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">随着时代的发展，企业进入全面数字化转型阶段，信息化建设越来越重要，新业务层出不穷，各种行业应用需求急剧膨胀，国内信息化建设已经进入高峰期，基础网络已成为各行业的基础性资源，是各行业信息化不可或缺的基础平台。</span></p><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">同天科技从成立以来一直致力于IT基础网络的建设，并与国内外一线ICT厂商（如思科、华为、juniper、H3C等）已成为战略合作伙伴，拥有众多专家级认证工程师。</span></p><p style=\"box-sizing: border-box;margin: 20px 0px !important;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">同天科技</span><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">从客户对网络的核心诉求出发，为客户提供一体化的企业网络基础架构解决方案，主要涵盖数据中心网络、备份中心网络、总部中心网络、分支机构网络、园区网、城域网、第三方外联网络、VPN网络等。</span></p><h3 style=\"box-sizing: border-box;font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">2.1.2&nbsp;</span><span style=\"box-sizing: border-box;font-family: 微软雅黑, sans-serif\">企业网典型案例</span></h3><p class=\"MsoListParagraph\" style=\"box-sizing: border-box;margin-top: 20px !important;margin-right: 0px !important;margin-bottom: 20px !important;margin-left: 28px;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">1)<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">民生卡部成都运营中心科技系统网络建设项目；</span></span></p><p class=\"MsoListParagraph\" style=\"box-sizing: border-box;margin-top: 20px !important;margin-right: 0px !important;margin-bottom: 20px !important;margin-left: 28px;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">2)<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">兰州商业银行两地三数据中心集成项目；</span></span></p><p class=\"MsoListParagraph\" style=\"box-sizing: border-box;margin-top: 20px !important;margin-right: 0px !important;margin-bottom: 20px !important;margin-left: 28px;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">3)<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">威海市商业银行备份中心网络集成项目；</span></span></p><p class=\"MsoListParagraph\" style=\"box-sizing: border-box;margin-top: 20px !important;margin-right: 0px !important;margin-bottom: 20px !important;margin-left: 28px;color: rgb(102, 102, 102);font-family: tahoma, PingFangSC-Regular, \" microsoft=\"\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">4)<span style=\"box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \" new=\"\" times=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"box-sizing: border-box\"><span style=\"box-sizing: border-box;font-size: 16px;font-family: 微软雅黑, sans-serif\">哈尔滨银行上海办事处设备采购项目。</span></span></p><p><br/></p>', 'f83082bd046641acb1b5c31cd65ffaed', '1', '2', '50', '0', '9f4b807db2e94670bb02cdc212ea7389', '2020-08-04 02:19:14', '9f4b807db2e94670bb02cdc212ea7389', '2020-08-04 02:31:27', '', '', '', '', '');
