INSERT INTO `system_category` VALUES ('d63c35e50d1f42f49904f263b5571a87', '使用说明', 'quickstart', '76crpo3s', '.76crpo3s', 'c17ddcad9fb149a6bf90f7bba0e0696b', '20190819\\7600b31a769b470d87a013254f85dccf.png', '', '1', '10', '1', 'quickstart', '/quickstart.html', '', '', '', 'ue', '####1.克隆项目到本地\r\n####2.导入Eclipse或Sts等开发工具\r\n####3.项目需要Redis，请自行修改application.yml中Redis配置\r\n####4.将项目主页附件标签页里的dreamer-blog.zip附件下载并解压到D盘根目录\r\n####5.运行项目DreamerBlogApplication.java\r\n####6.项目首页：http://localhost:8888\r\n####7.项目管理后台：http://localhost:8888/admin\r\n####8.管理后台用户名：wangjn；密码：123456', '<p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">CMS包括两个部分（代码部分、资源部分）代码不多说。资源就是图片、模版等，该目录在application.yml中web.resource-path配置项目中配置。视频教程：</span></p><p style=\"line-height: 2em;\"><br/></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">链接：https://pan.baidu.com/s/1plsfBl_ds_5TkVa-yRkESQ&nbsp;</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">提取码：2c8i</span></p><p style=\"line-height: 2em;\"><br/></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">1、克隆项目到本地工作空间</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">2、导入Eclipse或Sts等开发工具（推荐使用Spring Tools Suite 4）（项目中缺少的google/kaptcha.jar在附件中，大家自行下载）</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">3、项目需要Redis，请自行修改application.yml中Redis配置</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">4、修改项目资源目录，application.yml文件web.resource-path配置项（如D:/dreamer-cms/）</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">5、将项目src/main/resources/db/dreamer-cms.zip文件解压，保证解压后的目录路径的名称和资源目录一致</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">6、运行项目DreamerCMSApplication.java</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">7、网站首页：http://localhost:8888 项目管理后台：http://localhost:8888/admin</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">8、管理后台用户名：wangjn；密码：123456</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 16px;\">9、模版标签开发教程请参考：http://doc.itechyou.cn&nbsp;</span></p><p><br/></p>', '-1', '0', '1', '50', '0', '9f4b807db2e94670bb02cdc212ea7389', '2019-08-15 15:54:13', '9f4b807db2e94670bb02cdc212ea7389', '2020-08-04 09:29:02', '', '', '', '', '');
